mapper.select:
select username, email
from member
where userid = #{userid} and userpwd = #{userpwd}

select userid, userpwd, username, email
from member
where userid = #{userid}

mapper.update:
update member
set userpwd = #{userpwd}, username = #{username}, email = #{email}
where userid = #{userid}

mapper.select:
select userid, username, email
from member
order by userid desc;
